import React, {useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import { connect } from 'react-redux';
import SideBarcomponent from './SideBarcomponent';
import PropTypes from 'prop-types';

function Usertable(props) {

  const { title, columns, usersList} = props;

  const [modelData, setModelData]=useState({
    modelOpen:false,
    calanderDataList:usersList,
    showAddTable:false
  });  
    
    const handleClickModelOpen=(data)=>{
      console.log(data);
      let popupData=[data];
      setModelData({modelOpen:true,calanderDataList:popupData});
    }
    const handleClickModelClose=(data)=>{
      console.log(data);
      setModelData({...modelData, modelOpen:false, showAddTable:false});
    }
    const handleClicAddEvents=(data)=>{
      console.log(data);
      let popupData=[data];
      setModelData({modelOpen:true,calanderDataList:popupData,showAddTable:true});
    }
    // handleTableSearch = (searchedData) => {
      
    // }
    // const deleteSelectedRows=(seletedRowIds)=>{
    //   let filterData=usersList.filter((value)=>{
    //       return (!seletedRowIds.includes(value.user_index))
    //   });

    // }
  const actionColumn = [
    {
      title: "View events",
      // handleViewAction:{handleClickModelClose}
      callBack:handleClickModelOpen,
      icon:"view"
    },
    {
      title: "Add Events",
      //handleAddAction:{handleClickModelClose}
      callBack:handleClicAddEvents,
      icon:"add"
    }    
  ];
  const {modelOpen, calanderDataList, showAddTable}=modelData;
  return (
    <React.Fragment>
      <TableWidget
        rows={usersList}
        defaultSort='name'
        columns={columns}
        tableTitle={title}
        handleToolBarAction={props.deleteSelectedRows}
        actionColumn={actionColumn}
        handleTableSearch={handleTableSearch}      
      />
      <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
                    calanderDataList={calanderDataList}
                    showAddTable={showAddTable}
            />
    </React.Fragment>
  );
}
const mapStateToProps = (state) => {
  return {
    usersList: state.usersList
  }
}
const mapDispachToProps=(dispatch)=>{
    return {
      deleteSelectedRows:(seletedRowIds)=>{
        dispatch({type:"delete_user_data", data:seletedRowIds})
      },
      handleTableSearch:(searcheddata)=>{
        dispatch({type:"search_user_tables_data", data:searcheddata})
      }
    }
}
Usertable.propTypes={
  title:PropTypes.string.isRequired,
  columns:PropTypes.array.isRequired,
  usersList:PropTypes.array.isRequired,
  deleteSelectedRows:PropTypes.func.isRequired  
}
export default connect(mapStateToProps, mapDispachToProps)(Usertable);
