import React from 'react';
import TableWidget from '../widgets/TableWidget';
import { connect } from 'react-redux';


function Usertable(props) {

  const { title, columns, usersList, openModelClick } = props;

  const actionColumn = [
    {
      title: "View events",
      // handleViewAction:{handleClickModelClose}
      callBack: { handleClickModelClose }
    },
    {
      title: "Add Events",
      //handleAddAction:{handleClickModelClose}
      callBack: { handleClickModelClose }
    },
    {
      title: "Delete",
      //handleDeleteAction:{handleClickModelClose}
      callBack: { handleClickModelClose }
    },

  ]
  return (
    <React.Fragment>
      <TableWidget
        rows={usersList}
        defaultSort='name'
        columns={columns}
        tableTitle={title}
        actionColumn={actionColumn}
        openModelClick={openModelClick}

      />
    </React.Fragment>
  );
}
const mapStateToProps = (state) => {
  return {
    usersList: state.usersList
  }
}
export default connect(mapStateToProps)(Usertable);
