import React,{useState, useEffect} from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';
import SideBarcomponent from './SideBarcomponent';
import PropTypes from 'prop-types';

function Eventtable(props){
    let {title, columns, eventsList}=props;    
    
    let [modelData, setModelData]=useState({
      modelOpen:false,
      calanderDataList:eventsList,
      showAddTable:false,
      searchFilterValue:""
    });    

    useEffect(()=>{
      setModelData({
        ...modelData,
        calanderDataList:eventsList
      })

    },[eventsList])
    const handleClickModelOpen=(data)=>{
        console.log(data);
        let popupData=[data];
        setModelData({modelOpen:true,calanderDataList:popupData});
        //eventsArray=data;
        //setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelData({...modelData, modelOpen:false});
    }
    const actionColumn=[
        {
          title:"View event",
          // handleViewAction:{handleClickModelClose}
          callBack:handleClickModelOpen,
          icon:"view"
        },        
        {
          title:"Hide event on calander",          
          icon:"switch"
        }        
    ];
    const {modelOpen, calanderDataList, searchFilterValue}=modelData;
    
    return (
        <React.Fragment>            
            <TableWidget 
              rows={modelData.calanderDataList} 
              defaultSort='name'
              columns={columns}
              tableTitle={title}
              actionColumn={actionColumn}
              openModelClick={handleClickModelOpen} 
              handleToolBarAction={props.deleteSelectedRows}
              handleEventDisplayToggle={props.handleIsShowOnCalander}    
              handleTableSearch={handleTableSearch}
              searchPlaceholder="Search By Organizer"
              searchFilterValue={searchFilterValue}                      
            />
            <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
                    calanderDataList={calanderDataList}
            />
        </React.Fragment>
    );
}
Eventtable.propTypes={
  title:PropTypes.string.isRequired,
  columns:PropTypes.array.isRequired,
  eventsList:PropTypes.array.isRequired,
  deleteSelectedRows:PropTypes.func.isRequired,
  handleIsShowOnCalander:PropTypes.func.isRequired
}
export default connect(mapStateToProps, mapDispachToProps)(Eventtable);
