const initialState = {
    usersList: [],
    eventsList: []
}

const reducer = (state = initialState, action) => {
    let newState = { ...state };
    let filterData="";
    switch (action.type) {
        case "get_user_event_data":
            newState = {
                ...state,
                usersList: action.data[0].UserRows,
                eventsList: action.data[1].EventRows
            };
            return newState;
        case "delete_user_data":
            filterData = state.usersList.filter((value) => {
                return (!action.data.includes(value.index))
            });
            newState = {
                ...state,
                usersList: filterData
            };
            return newState;
        case "delete_event_data":
            filterData = state.eventsList.filter((value) => {
                return (!action.data.includes(value.index))
            });
            newState = {
                ...state,
                eventsList: filterData
            };
            return newState;
        default:
            return newState;
    }
}
export default reducer
