import React from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';



function Eventtable(props){
    const {title, columns, eventsList}=props;    

    const [modelOpen, setModelOpen]=useState(false);
    const handleClickModelOpen=(data)=>{
        console.log(data);
        setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelOpen(false);
    }

    return (
        <React.Fragment>            
            <TableWidget 
              rows={eventsList} 
              defaultSort='name'
              columns={columns}
              tableTitle={title}
              openModelClick={openModelClick}            
            />
            <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
            />
        </React.Fragment>
    );
}

const mapStateToProps=(state)=>{
  return {
    eventsList:state.eventsList
  }
}
export default connect(mapStateToProps)(Eventtable);
