import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import { CssBaseline, Container, Box } from '@material-ui/core';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import PropTypes from 'prop-types';
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
const useStyles = makeStyles(theme => ({
  padding:"100px",
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
    flexDirection: 'rowReverse'
  },
}));

const localizer = momentLocalizer(moment)
const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function SideBarcomponent(props) {
  //const [modelOpen, closeModelClick]=props;
  const classes = useStyles();
  //const [modelOpen, setOpen] = React.useState(false);

  //   const handleClickOpen = () => {
  //     setOpen(true);
  //   };

  // const closeModelClick = () => {
  //   setOpen(false);
  // };
  const eventsArray = [
    {
      start: new Date(),
      end: new Date(moment().add(1, "hours")),
      eventClasses: 'optionalEvent',
      title: 'test event',
      description: 'This is a test description of an event',
    },
    {
      start: new Date(moment().add(1, "days")),
      end: new Date(moment().add(1, "days").add(2, "hours")),
      title: 'test event',
      description: 'This is a test description of an event',
      data: 'you can add what ever random data you may want to use later',
    },
  ];
  const handleSelect = ({ start, end }) => {

  }

  return (
    <div>
      {/* <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open full-screen dialog
      </Button> */}
      <Container>
        <Box my={2}>
          <div style={{padding:"5px"}}>
            <Dialog fullScreen open={props.modelOpen} onClose={props.closeModelClick} TransitionComponent={Transition}>
              <AppBar className={classes.appBar}>
                <Toolbar>
                  <h2>Events </h2>
                  <IconButton edge="start" color="inherit" onClick={props.closeModelClick} aria-label="close">
                    <CloseIcon />
                  </IconButton>
                </Toolbar>
              </AppBar>
              <CssBaseline />
              <React.Fragment>
                <Calendar
                  localizer={localizer}
                  defaultDate={new Date()}
                  defaultView="month"
                  events={eventsArray}
                  style={{ height: "100vh" }}
                  onSelectEvent={event => alert(event.title)}
                  onSelectSlot={handleSelect}
                />
              </React.Fragment>
            </Dialog>
          </div>
        </Box>
      </Container>

    </div>
  );
}

export default SideBarcomponent;
