import React,{useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';
import SideBarcomponent from './SideBarcomponent';
import PropTypes from 'prop-types';
import { eventColumns } from '../data/EventData';
function AddEventsTable(props){
    const {eventsList}=props;
    const getFilterdata=(eventsList)=>{
       let data= eventsList.filter((element)=>{
            retrun (element.registerd_user.length < element.capacity);
        });
        return data;
    }    
    const filterdata=getFilterdata(eventsList);
    return (
        <React.Fragment>            
            <TableWidget 
              rows={filterdata} 
              defaultSort='name'
              columns={eventColumns}
              tableTitle={"Add Evvents"}             
              handleToolBarAction={props.addSelectedRows}                                    
            />            
        </React.Fragment>
    );
}

const mapStateToProps=(state)=>{
  return {
    eventsList:state.eventsList
  }
}
const mapDispachToProps=(dispatch)=>{
  return {
    addSelectedRows:(seletedRowIds)=>{
      dispatch({type:"add_event_data", data:seletedRowIds})
    }
  }
}
AddEventsTable.propTypes={
  title:PropTypes.string.isRequired,
  columns:PropTypes.array.isRequired,
  eventsList:PropTypes.array.isRequired,
  addSelectedRows:PropTypes.func.isRequired  
}
export default connect(mapStateToProps, mapDispachToProps)(AddEventsTable);
