import React from 'react';
import TableWidget from '../widgets/TableWidget'
function createData(organizer, company, about, scheduledfor, duration, capacity) {
    return { organizer, company, about, scheduledfor, duration, capacity };
  }
  
  const rows = [
    createData('Cupcake', 'spectrum Global', 'Example event 1', '5-12-2019',2),
    createData('Donut', 'spectrum Global', 'Example event 2', '5-12-2019', 2),
    createData('Eclair', 'spectrum Global', 'Example event 3', '5-12-2019',5),
    createData('Frozen yoghurt', 'spectrum Global', 'Example event 4', '5-12-2019',3),
    createData('Gingerbread', 'spectrum Global', 'Example event 5', '5-12-2019',4),
    createData('Honeycomb', 'spectrum Global', 'Example event 6', '5-12-2019',5),
    createData('Ice cream sandwich', 'spectrum Global', 'Example event 7', '5-12-2019',6),
    createData('Jelly Bean', 'spectrum Global', 'Example event 8', '5-12-2019',5),
    createData('KitKat', 'spectrum Global', 'Example event 9', '5-12-2019',5),
    createData('Lollipop', 'spectrum Global', 'Example event 10', '5-12-2019',5),
    createData('Marshmallow', 'spectrum Global','Example event 11', '5-12-2019',5),
    createData('Nougat', 'spectrum Global', 'Example event 12', '5-12-2019',5),
    createData('Oreo', 'spectrum Global', 'Example event 13', '5-12-2019',5),
  ];

const columns = [
    { key_id: 'organizer', numeric: false, disablePadding: true, label: 'Organizer', sortFlag:true },
    { key_id: 'company',  numeric: true, disablePadding: false, label: 'Company', sortFlag:true },
    { key_id: 'about', numeric: true, disablePadding: false, label: 'About', sortFlag:false },
    { key_id: 'scheduledfor', numeric: false, disablePadding: false, label: 'Scheduled For', sortFlag:false },
    { key_id: 'duration', numeric: false, disablePadding: false, label: 'Duration', sortFlag:false },
    { key_id: 'capacity', numeric: true, disablePadding: false, label: 'Capacity', sortFlag:false}
  ];


function Eventtable(){
    const tableTitle="Event List";
    return (
        <React.Fragment>            
            <TableWidget 
              rows={rows} 
              defaultSort='name'
              columns={columns}
              tableTitle={tableTitle}            
            />
        </React.Fragment>
    );
}

export default Eventtable;
