import React, {useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import { connect } from 'react-redux';
import SideBarcomponent from './SideBarcomponent';


function Usertable(props) {

  const { title, columns, usersList} = props;

  const [modelOpen, setModelOpen]=useState(false);
    
    const handleClickModelOpen=(data)=>{
        console.log(data);
        setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelOpen(false);
    }
  const actionColumn = [
    {
      title: "View events",
      // handleViewAction:{handleClickModelClose}
      callBack:handleClickModelOpen
    },
    {
      title: "Add Events",
      //handleAddAction:{handleClickModelClose}
      callBack:handleClickModelOpen
    },
    {
      title: "Delete",
      //handleDeleteAction:{handleClickModelClose}
      callBack:handleClickModelOpen
    },

  ]
  return (
    <React.Fragment>
      <TableWidget
        rows={usersList}
        defaultSort='name'
        columns={columns}
        tableTitle={title}
        actionColumn={actionColumn}        
      />
      <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
            />
    </React.Fragment>
  );
}
const mapStateToProps = (state) => {
  return {
    usersList: state.usersList
  }
}
export default connect(mapStateToProps)(Usertable);
