const initialState = {
    usersList: [],
    eventsList: []
}

const reducer = (state = initialState, action) => {
    let newState = { ...state };
    let filterData = "";
    let searcheddata="";
    let flag=false;
    let regex="";
    let userFilterData="";
    switch (action.type) {
        case "get_user_event_data":
            newState = {
                ...state,
                usersList: action.data[0].UserRows,
                eventsList: action.data[1].EventRows
            };
            return newState;
        case "delete_user_data":

            userFilterData= state.usersList.filter((value) => {
                return (!action.data.includes(value.index))
            });

            filterData=state.eventsList;
            filterData.forEach((element)=>{              
               action.data.forEach((value)=>{
                 let valueindex=element.registerd_user.indexOf(value);
                 if(valueindex !== -1){
                    element.registerd_user.splice(valueindex, 1);
                 }
               });               
            })
            newState = {
                ...state,
                usersList: userFilterData,
                eventsList:filterData
            };
            return newState;
        case "delete_event_data":
            filterData = state.eventsList.filter((value) => {
                return (!action.data.includes(value.index))
            });
            newState = {
                ...state,
                eventsList: filterData
            };
            return newState;
        case "set_isShowOnCalander":
            filterData = state.eventsList.map((value) => {
                if(value.index === action.data){
                    value.isShowOnCalander= !value.isShowOnCalander;
                }
                return value;
            });
            newState = {
                ...state,
                eventsList: filterData
            };
            return newState;
        case "add_event_data":
                let selectedEvents=action.data.eventsId;
                let selectedUser=action.data.addUserData;
                userFilterData=state.usersList;
                let selectedEventsLength=selectedEvents.lenth;
            filterData = state.eventsList.map((value) => {
                if(selectedEvents.includes(value.index)){
                    value.registerd_user.push(selectedUser.index);                    
                }
                return value;
            });
           userFilterData = state.usersList.map((value) => {
                if(value.index === selectedUser.index){
                    value.registeredevents=value.registeredevents+selectedEventsLength;                    
                }
                return value;
            });
            newState = {
                ...state,
                eventsList: filterData,
                usersList: userFilterData
            };
            return newState;
        case "search_user_tables_data":
                searcheddata=action.data;
                let user_ids_user=[];
                regex = new RegExp(searcheddata, 'gi')
                flag=false;

               userFilterData = state.usersList.filter((value) => {
                    flag=value.name.match(regex)
                    if(flag){
                        user_ids_user.push(value.index);
                        return true;
                    }else{
                     return false;
                    }                        
                });

               filterData = state.eventsList.filter((value) => {
                    const ids_length=value.registerd_user.length;
                    flag=false;
                    for(let i=0; i<ids_length; i++){
                        flag=user_ids_user.includes(value.registerd_user[i]);
                            if(flag)break;
                    }
                    return flag;
                                    
                });

            newState = {
                ...state,
                eventsList: filterData,
                usersList: userFilterData
            };
            return newState;
            case "search_event_tables_data":
                searcheddata=action.data;
                let user_ids_event=[];
                flag=false;
                regex = new RegExp(searcheddata, 'gi')


                filterData= state.eventsList.filter((value) => {
                    flag=value.organizer.match(regex)
                    if(flag){
                        //events_ids.push(value.index);
                        user_ids_event=[...new Set([...user_ids_event, ...value.registerd_user])];
                        return true;                        
                    }else{
                     return false;
                    }
                                            
                });

               userFilterData = state.usersList.filter((element) => {
                    return user_ids_event.includes(element.index);                                    
                });

            newState = {
                ...state,
                eventsList: filterData,
                usersList: userFilterData
            };
            return newState;
        
        default:                
            return newState;
    }
}
export default reducer
