import React,{useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';
import SideBarcomponent from './SideBarcomponent';


function Eventtable(props){
    const {title, columns, eventsList}=props;    

    const [modelOpen, setModelOpen]=useState(false);
    
    const handleClickModelOpen=(data)=>{
        console.log(data);
        setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelOpen(false);
    }
    const actionColumn=[
        {
          title:"View event",
          // handleViewAction:{handleClickModelClose}
          callBack:handleClickModelOpen,
          icon:"view"
        },
        {
          title:"Add members",
          //handleAddAction:{handleClickModelClose}
          callBack:handleClickModelOpen,
          icon:"delete"
        },
        {
          title:"hide event on calander",
          //handleAddAction:{handleClickModelClose}
          callBack:handleClickModelOpen,
          icon:"switch"
        }        
    ]
    return (
        <React.Fragment>            
            <TableWidget 
              rows={eventsList} 
              defaultSort='name'
              columns={columns}
              tableTitle={title}
              actionColumn={actionColumn}
              openModelClick={handleClickModelOpen} 
              handleDeleteRows={props.deleteSelectedRows}           
            />
            <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
            />
        </React.Fragment>
    );
}

const mapStateToProps=(state)=>{
  return {
    eventsList:state.eventsList
  }
}
const mapDispachToProps=(dispatch)=>{
  return {
    deleteSelectedRows:(seletedRowIds)=>{
      dispatch({type:"delete_user_data", data:seletedRowIds})
    }
  }
}
export default connect(mapStateToProps, mapDispachToProps)(Eventtable);
