import React,{useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';
import SideBarcomponent from './SideBarcomponent'


function Eventtable(props){
    const {title, columns, eventsList}=props;    

    const [modelOpen, setModelOpen]=useState(false);
    
    const handleClickModelOpen=(data)=>{
        console.log(data);
        setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelOpen(false);
    }
    const actionColumn=[
        {
          title:"View events",
          // handleViewAction:{handleClickModelClose}
          callBack:{handleClickModelOpen}
        },
        {
          title:"Add Events",
          //handleAddAction:{handleClickModelClose}
          callBack:{handleClickModelOpen}
        },
        {
          title:"Delete",
          //handleDeleteAction:{handleClickModelClose}
          callBack:{handleClickModelOpen}
        },
        
    ]
    return (
        <React.Fragment>            
            <TableWidget 
              rows={eventsList} 
              defaultSort='name'
              columns={columns}
              tableTitle={title}
              actionColumn={actionColumn}
              openModelClick={handleClickModelOpen}            
            />
            <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
            />
        </React.Fragment>
    );
}

const mapStateToProps=(state)=>{
  return {
    eventsList:state.eventsList
  }
}
export default connect(mapStateToProps)(Eventtable);
