import React,{useState} from 'react';
import TableWidget from '../widgets/TableWidget';
import {connect} from 'react-redux';
import SideBarcomponent from './SideBarcomponent';
import PropTypes from 'prop-types';

function Eventtable(props){
    let {title, columns, eventsList}=props;    
    
    let [modelData, setModelData]=useState({
      modelOpen:false,
      calanderDataList:eventsList,
      showAddTable:false
    });    

    const handleClickModelOpen=(data)=>{
        console.log(data);
        let popupData=[data];
        setModelData({modelOpen:true,calanderDataList:popupData});
        //eventsArray=data;
        //setModelOpen(true);
    }
    const handleClickModelClose=(data)=>{
        console.log(data);
        setModelData({...modelData, modelOpen:false});
    }
    const actionColumn=[
        {
          title:"View event",
          // handleViewAction:{handleClickModelClose}
          callBack:handleClickModelOpen,
          icon:"view"
        },        
        {
          title:"Hide event on calander",          
          icon:"switch"
        }        
    ];
    const {modelOpen, calanderDataList}=modelData;
    const handleTableSearch=(value)=>{
        let searcheddata=value;
                let user_ids_event=[];
                let flag=false;
                let regex = new RegExp(searcheddata, 'gi')


                let filterData= modelData.calanderDataList.filter((value) => {
                    flag=value.organizer.match(regex)
                    if(flag){
                        //events_ids.push(value.index);
                        user_ids_event=[...new Set([...user_ids_event, ...value.registerd_user])];
                        return true;                        
                    }else{
                     return false;
                    }
                                            
                });

                setModelData({...modelData, calanderDataList:filterData});
            //    userFilterData = state.usersList.filter((element) => {
            //         return user_ids_event.includes(element.index);                                    
            //     });

            // newState = {
            //     ...state,
            //     eventsList: filterData,
            //     usersList: userFilterData
            // };
            
    }
    return (
        <React.Fragment>            
            <TableWidget 
              rows={modelData.calanderDataList} 
              defaultSort='name'
              columns={columns}
              tableTitle={title}
              actionColumn={actionColumn}
              openModelClick={handleClickModelOpen} 
              handleToolBarAction={props.deleteSelectedRows}
              handleEventDisplayToggle={props.handleIsShowOnCalander}    
              handleTableSearch={handleTableSearch}
              searchPlaceholder="Search By Organizer"                      
            />
            <SideBarcomponent 
                    modelOpen={modelOpen}                    
                    closeModelClick={handleClickModelClose}
                    calanderDataList={calanderDataList}
            />
        </React.Fragment>
    );
}

const mapStateToProps=(state)=>{
  return {
    eventsList:state.eventsList
  }
}
const mapDispachToProps=(dispatch)=>{
  return {
    deleteSelectedRows:(seletedRowIds)=>{
      dispatch({type:"delete_event_data", data:seletedRowIds})
    },
    handleIsShowOnCalander:(row_id)=>{
      dispatch({type:"set_isShowOnCalander", data:row_id})
    }

    //* handleTableSearch:(searcheddata)=>{
    //     dispatch({type:"search_events_tables_data", data:searcheddata})
    // }*/

  }
}
Eventtable.propTypes={
  title:PropTypes.string.isRequired,
  columns:PropTypes.array.isRequired,
  eventsList:PropTypes.array.isRequired,
  deleteSelectedRows:PropTypes.func.isRequired,
  handleIsShowOnCalander:PropTypes.func.isRequired
}
export default connect(mapStateToProps, mapDispachToProps)(Eventtable);
